export default {
  data() {
    return {
      text: '',
      message: '',
      messages: [],
      sessionID: 'sess1',
      wsText: null,
      wsAudio: null,
      audioCtx: null,
      audioPlayingNodes: [],
      userInteracted: false,
      // 用于第一次交互激活 AudioContext
      audioQueue: [],
      isPlayingAudio: false,
      nextPlayTime: 0,
      audioSampleRate: 24000
    };
  },
  computed: {
    showMessages() {
      const all = [...this.messages];
      if (this.message.trim()) {
        all.push({
          role: 'assistant',
          content: this.message
        });
      }
      return all;
    }
  },
  methods: {
    sendText() {
      if (!this.text.trim()) return;
      if (!this.userInteracted) {
        this.resumeAudioContext();
        this.userInteracted = true;
      }
      this.wsText.send(this.text.trim());
      this.messages.push({
        role: 'user',
        content: this.text.trim()
      });
      this.text = '';
    },
    resumeAudioContext() {
      if (this.audioCtx && this.audioCtx.state === 'suspended') {
        this.audioCtx.resume();
      }
    },
    setupTextSocket() {
      this.wsText = new WebSocket(`ws://localhost:8080/ws/text-stream?sessionid=${this.sessionID}`);
      this.wsText.onopen = () => {
        console.log('文本 WebSocket 已连接');
      };
      this.wsText.onmessage = event => {
        const chunk = event.data;
        this.message += chunk;
        if (chunk === '\n\n') {
          this.messages.push({
            role: 'assistant',
            content: this.message
          });
          this.message = '';
        }
      };
      this.wsText.onerror = e => {
        console.error('文本 WebSocket 错误', e);
      };
      this.wsText.onclose = () => {
        console.log('文本 WebSocket 已关闭');
      };
    },
    setupAudioSocket() {
      this.audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      this.audioQueue = [];
      this.isPlayingAudio = false;
      this.nextPlayTime = 0;
      this.wsAudio = new WebSocket(`ws://localhost:8080/ws/audio-stream?sessionid=${this.sessionID}`);
      this.wsAudio.binaryType = 'arraybuffer';
      this.wsAudio.onopen = () => {
        console.log('音频 WebSocket 已连接');
      };
      this.wsAudio.onmessage = event => {
        if (!this.userInteracted) {
          this.resumeAudioContext();
          this.userInteracted = true;
        }
        try {
          const arrayBuffer = event.data;
          console.log('接收到音频数据，字节长度:', arrayBuffer.byteLength);

          // 转换为AudioBuffer
          const pcmData = new Int16Array(arrayBuffer);
          const float32Data = new Float32Array(pcmData.length);
          for (let i = 0; i < pcmData.length; i++) {
            float32Data[i] = pcmData[i] / 32768.0;
          }
          const audioBuffer = this.audioCtx.createBuffer(1, float32Data.length, this.audioSampleRate);
          audioBuffer.getChannelData(0).set(float32Data);

          // 添加到队列而不是立即播放
          this.audioQueue.push(audioBuffer);
          console.log(`音频块加入队列，队列长度: ${this.audioQueue.length}`);

          // 如果当前没有播放，开始播放
          if (!this.isPlayingAudio) {
            this.playAudioQueue();
          }
        } catch (err) {
          console.error('处理音频数据失败:', err);
        }
      };
      this.wsAudio.onerror = e => {
        console.error('音频 WebSocket 错误', e);
      };
      this.wsAudio.onclose = () => {
        console.log('音频 WebSocket 已关闭');
        this.stopAllAudio();
        this.isPlayingAudio = false;
      };
    },
    // 新增方法：连续播放音频队列
    playAudioQueue() {
      if (this.audioQueue.length === 0) {
        this.isPlayingAudio = false;
        return;
      }
      this.isPlayingAudio = true;
      const audioBuffer = this.audioQueue.shift();
      const source = this.audioCtx.createBufferSource();
      source.buffer = audioBuffer;
      source.connect(this.audioCtx.destination);

      // 计算播放时间，确保连续播放
      const currentTime = this.audioCtx.currentTime;
      const startTime = Math.max(currentTime, this.nextPlayTime);
      source.onended = () => {
        // 从播放列表中移除
        const idx = this.audioPlayingNodes.indexOf(source);
        if (idx !== -1) this.audioPlayingNodes.splice(idx, 1);

        // 继续播放队列中的下一个音频块
        setTimeout(() => this.playAudioQueue(), 10); // 小延迟防止卡顿
      };
      this.audioPlayingNodes.push(source);
      source.start(startTime);

      // 更新下次播放时间
      this.nextPlayTime = startTime + audioBuffer.duration;
      console.log(`播放音频块，时长: ${audioBuffer.duration.toFixed(3)}秒，开始时间: ${startTime.toFixed(3)}秒`);
    },
    stopAllAudio() {
      // 停止所有正在播放的音频
      this.audioPlayingNodes.forEach(node => {
        try {
          node.stop();
        } catch (e) {
          console.error('停止音频节点失败:', e);
        }
      });
      this.audioPlayingNodes = [];

      // 清空队列
      this.audioQueue = [];
      this.isPlayingAudio = false;
      this.nextPlayTime = 0;
      console.log('所有音频已停止，队列已清空');
    }
  },
  mounted() {
    // 监听用户交互，保证 AudioContext 激活
    window.addEventListener('click', () => {
      if (!this.userInteracted) {
        this.resumeAudioContext();
        this.userInteracted = true;
      }
    }, {
      once: true
    });
    this.setupTextSocket();
    this.setupAudioSocket();
  },
  beforeDestroy() {
    if (this.wsText) this.wsText.close();
    if (this.wsAudio) this.wsAudio.close();
    this.stopAllAudio();
    if (this.audioCtx) this.audioCtx.close();
  }
};
