export default {
  data() {
    return {
      text: '',
      message: '',
      messages: [],
      sessionID: 'sess1',
      // 你实际的 sessionid
      wsText: null,
      wsAudio: null,
      audioCtx: null,
      audioPlayingNodes: [] // 当前播放的 AudioBufferSourceNode 列表
    };
  },
  computed: {
    showMessages() {
      // 创建消息列表副本
      const all = [...this.messages];

      // 在副本上做更新
      if (this.message.trim()) {
        all.push({
          role: 'assistant',
          content: this.message
        });
      }
      return all;
    }
  },
  methods: {
    sendText() {
      if (!this.text.trim()) return;
      if (this.wsText && this.wsText.readyState === WebSocket.OPEN) {
        this.wsText.send(this.text.trim());

        // 显示用户消息
        this.messages.push({
          role: 'user',
          content: this.text.trim()
        });
        this.text = '';
      }
    },
    setupTextSocket() {
      this.wsText = new WebSocket(`ws://localhost:8080/ws/text-stream?sessionid=${this.sessionID}`);
      this.wsText.onopen = () => {
        console.log('文本 WebSocket 已连接');
      };
      let partialMsg = '';
      this.wsText.onmessage = event => {
        const chunk = event.data;
        partialMsg += chunk;

        // 假设后端用 "\n\n" 来表示消息结束
        if (partialMsg.endsWith('\n\n')) {
          // 去掉末尾换行
          const msg = partialMsg.trim();
          this.messages.push({
            role: 'assistant',
            content: msg
          });
          partialMsg = '';
        }
      };
      this.wsText.onerror = e => {
        console.error('文本 WebSocket 错误', e);
      };
      this.wsText.onclose = () => {
        console.log('文本 WebSocket 已关闭');
      };
    },
    setupAudioSocket() {
      this.audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      this.wsAudio = new WebSocket(`ws://localhost:8080/ws/audio-stream?sessionid=${this.sessionID}`);
      this.wsAudio.binaryType = 'arraybuffer';
      this.wsAudio.onopen = () => {
        console.log('音频 WebSocket 已连接');
      };
      this.wsAudio.onmessage = async event => {
        try {
          const arrayBuffer = event.data;
          // PCM 16bit little endian, 16kHz, mono

          // 转为 Int16Array
          const pcmData = new Int16Array(arrayBuffer);
          // 转换为 Float32Array [-1,1]
          const float32Data = new Float32Array(pcmData.length);
          for (let i = 0; i < pcmData.length; i++) {
            float32Data[i] = pcmData[i] / 32768;
          }

          // 创建 AudioBuffer
          const audioBuffer = this.audioCtx.createBuffer(1, float32Data.length, 16000);
          audioBuffer.getChannelData(0).set(float32Data);

          // 创建播放节点
          const source = this.audioCtx.createBufferSource();
          source.buffer = audioBuffer;
          source.connect(this.audioCtx.destination);

          // 播放完从列表移除，防止内存泄漏
          source.onended = () => {
            const idx = this.audioPlayingNodes.indexOf(source);
            if (idx !== -1) this.audioPlayingNodes.splice(idx, 1);
          };
          this.audioPlayingNodes.push(source);
          source.start();
        } catch (err) {
          console.error('播放PCM数据失败:', err);
        }
      };
      this.wsAudio.onerror = e => {
        console.error('音频 WebSocket 错误', e);
      };
      this.wsAudio.onclose = () => {
        console.log('音频 WebSocket 已关闭');
        this.stopAllAudio();
      };
    },
    stopAllAudio() {
      // 停止所有正在播放的音频节点
      this.audioPlayingNodes.forEach(node => {
        try {
          node.stop();
        } catch (e) {
          console.error('停止音频节点失败:', e);
        }
      });
      this.audioPlayingNodes = [];
    }
  },
  mounted() {
    this.setupTextSocket();
    this.setupAudioSocket();
  },
  beforeDestroy() {
    if (this.wsText) this.wsText.close();
    if (this.wsAudio) this.wsAudio.close();
    this.stopAllAudio();
    if (this.audioCtx) this.audioCtx.close();
  }
};
